INSERT INTO guides VALUES (null, 'Alimentos', 'Esta guía le proporcionara al paciente las sustancias que contienen los alimentos y como pueden o no ayudarlo en su organismo.', 19, 'http://ezpo.tech/kidney/Kidneys/Images/Guides/GuiaDeAlimentacion.jpg', 'http://ezpo.tech/kidney/Kidneys/Pdf/Alimentos.pdf', 'Kidneys', 2018, 'http://uvadoc.uva.es/ handle/10324/24513, juliaarribasalonso@gmail.com', '<html> <body> <ul> <li>1.Introduccion</li> <li>2.Alimentacion renal del paciente.</li> <li>3.¿Tendre que seguir una dieta especial?</li> <li>3.1.Los hidratos de carbono.</li> <li>3.2.Lipidos o grasas.</li> <li>3.3.El calcio y el fosforo.</li> <li>3.4.Potasio.</li> <li>4.¿Como reducir el potasio?</li> <li>4.1.Sodio</li> <li>5.Alimentos restringidos.</li> <li>6.Recomendaciones para el paciente.</li> <li>7.Recomendaciones dieteticas.</li> <li>Referencias bibliograficas.</li> </ul> </body> </html>');
INSERT INTO guides VALUES (null, 'Cuidados', 'La presente guía contiene las recomendaciones básicas para el cuidado físico del paciente, las medidas de higiene y manipulación del catéter.', 12, 'http://ezpo.tech/kidney/Kidneys/Images/Guides/GuiaDeHabitacion.jpg', 'http://ezpo.tech/kidney/Kidneys/Pdf/Cuidados.pdf', 'Kidneys', 2018, 'https:// www.kidney.org/sites/default/files/docsperitonealdialysis_span.pdf', '<html> <body> <ul> <li>1.Introduccion</li> <li>2.Cuidados para pacientes con insuficiencia renal.</li> <li>3.Cuidados previos al cambio de solucion o uso de cateter.</li> <li>4.Sugerencias para el cuidado habitual del cateter.</li> <li>5.Normas de dialisis peritoneal.</li> <li>6.Infraestructura y espacio fisico general.</li> <li>7.Requisitos para dialisis peritoneal.</li> <li>8.Referencias Bibliograficas.</li> </ul> </body> </html>');
INSERT INTO guides VALUES (null, 'DPA', 'Si usted fue diagnosticado con insuficiencia renal crónica tendrá muchas dudas con respecto a lo que pueda pasar con su vida es por eso que son de tanta utilidad las guías que Kidneys ha creado para ellos.', 11, 'http://ezpo.tech/kidney/Kidneys/Images/Guides/GuiaDPA.jpg', 'http://ezpo.tech/kidney/Kidneys/Pdf/DPA.pdf', 'Kidneys', 2018, 'https:// www.Copyright.com/165201100b2c11cd? projector=1&messagePartId=0.2', '<html> <body> <ul> <li>1.Introduccion</li> <li>2.¿Que es la insuficiencia renal?</li> <li>3.Materiales para la aplicacion del tratamiento de DPA</li> <li>4.Proceso del tratamiento de DPA</li> <li>5.Orificio de acceso peritoneal</li> <li>6.Referencias bibliograficas</li> </ul> </body> </html>');
INSERT INTO guides VALUES (null, 'DPCC', 'El tratamiento de dialisis automatizado es una gran opcion para que el paciente pueda realizar su tratamiento por las noches y poder tener una vida productiva por el dia.', 10, 'http://ezpo.tech/kidney/Kidneys/Images/Guides/GuiaDPCC.jpg', 'http://ezpo.tech/kidney/Kidneys/Pdf/DPCC.pdf', 'Kidneys', 2018, 'http://alcersalamanca.org/Documentos/ modalidades_cordoba.pdf', '<html> <body> <ul> <li>1.Introduccion</li> <li>2.Dialisis peritoneal continua ciclica</li> <li>3.Materiales utilizados en la aplicacion del tratamiento DPCC</li> <li>4.Proceso del tratamiento DPCC</li> <li>5.Referencias bibliograficas</li> </ul> </body> </html>');
